package gbicc.irs.esb.service.support;import com.dc.eai.conv.InputPacket;import com.dc.eai.conv.OutputPacket;import com.dc.eai.data.CompositeData;import com.dcfs.esb.client.converter.StandardConverter;public class PackUtil{	private static StandardConverter standardConverter = new StandardConverter();	/**	 * 功能 : 组包	 * 	 * @param request	 * @return	 */	public static byte[] pack(CompositeData request)	{		OutputPacket outputPacket = new OutputPacket();		standardConverter.pack(outputPacket, request, null);		return outputPacket.getBuff();	}		/**	 * 功能 : 拆包	 * 	 * @param request	 * @return	 */	public static CompositeData unpack(byte[] request)	{		CompositeData response = new CompositeData();		standardConverter.unpack(new InputPacket(request), response, null);		return response;	}}